import React from "react";
import { useHistory } from "react-router-dom";
import { Table, Input } from "reactstrap";
import Tickbox from "../elements/Tickbox.js";
import CustomNavbar from "../components/CustomNavbar";
import "../styles/HomePage.css";
import $ from "jquery";
import * as constants from "../constants";

export default function HomePage() {
  const history = useHistory();
  const [user, setUser] = React.useState("");
  const token = localStorage.getItem("token");

  React.useEffect(() => {
    if (token) {
      fetch(constants.dbstring + "/get-current-user", {
        headers: { Authorization: `Bearer ${token}` },
      })
        .then((res) => res.json())
        .then((u) => {
          setUser(u["username"]);
        });
    }
  }, [token]);

  React.useEffect(() => {
    if (token) {
      fetch(constants.dbstring + "/" + user, {
        headers: { Authorization: `Bearer ${token}` },
      })
        .then((res) => res.json())
        .then((u) => {
          setUsers(u);
        });
    } else {
      history.push("/vendor-login");
    }
  }, [token]);

  return (
    <div>
      <CustomNavbar />

      <div className="content">
        <Table hover>
          <thead>
            <tr>
              <th>Read</th>
              <th>Title</th>
              <th>Author</th>
              <th>Rating</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td className="isread" scope="row">
                <Tickbox />
              </td>
              <td>
                <input
                  className="table-editable title"
                  type="text"
                  value={"War & Peace"}
                  disabled={true}
                />
              </td>
              <td>
                <input
                  className="table-editable author"
                  type="text"
                  value={"Leo Tolstoy"}
                  disabled={true}
                />
              </td>
              <td>
                <input
                  className="table-editable rating"
                  type="text"
                  value={"10"}
                  disabled={true}
                />
              </td>
            </tr>
          </tbody>
        </Table>
      </div>
    </div>
  );
}
