import React from "react";
import "../styles/components/BookSearch.css";

export default function BookSearch() {
  const [loading, setLoading] = React.useState(true);
  const [query, setQuery] = React.useState("");
  const [searchResults, setSearchResults] = React.useState([]);

  React.useEffect(() => {
    const formattedQuery = query.replace(" ", "+");
    const url = `http://openlibrary.org/search.json?title=${formattedQuery}`;

    fetch(url)
      .then((res) => res.json())
      .then((res) => {
        setSearchResults(res.docs);
        setLoading(false);
        console.log(res);
      });
  });

  return (
    <div>
      <h2>Book Search</h2>
      <input
        placeholder="Search"
        onChange={(e) => setSearchTitle(e.target.value)}
      />
      <hr />
      <div className="book-suggestions">
        {loading
          ? ""
          : searchResults.slice(0, 5).map((book) => {
              return (
                <span className="search-suggestion">
                  {book.title} ({book.author_name[0]}) , &nbsp;
                  {book.publisher[0]} ({book.publish_date[0]})
                </span>
              );
            })}
      </div>
    </div>
  );
}
