import React from "react";

export default function BookSearch() {
  const [loading, setLoading] = React.useState(true);
  const [searchTitle, setSearchTitle] = React.useState("");
  const [searchResults, setSearchResults] = React.useState(Object);

  React.useEffect(() => {
    console.log(searchTitle);
    const formattedTitle = searchTitle.replace(" ", "+");
    const url = `http://openlibrary.org/search.json?title=${formattedTitle}`;

    if (formattedTitle.length() >= 4) {
      fetch(url)
        .then((res) => res.json())
        .then((res) => {
          setSearchResults(res);
          setLoading(false);
          console.log(res);
        });
    }
  });

  return (
    <div>
      <input type="title" placeholder="Title" onChange={setSearchTitle} />
      <hl />
      {loading
        ? ""
        : searchResults.docs.map((book) => {
            return book.title;
          })}
    </div>
  );
}
