import React from "react";
import { useHistory } from "react-router-dom";
import CustomNavbar from "../components/CustomNavbar";
import "../styles//pages/HomePage.css";
import Footer from "../components/Footer";

import IsbnSearch from "../components/IsbnSearch";

export default function HomePage() {
  const history = useHistory();

  const [isbn, setIsbn] = React.useState("0099922800");
  const [IsbnSearchBookdata, setIsbnSearchBookdata] = React.useState(Object);
  const [isbnSearchLoading, setIsbnSearchLoading] = React.useState(false);
  const [isbnSearchLoaded, setIsbnSearchLoaded] = React.useState(false);

  function handleSubmit(e) {
    e.preventDefault();
    setIsbnSearchLoading(true);

    const url =
      "https://openlibrary.org/api/books?bibkeys=ISBN:" +
      isbn +
      "&jscmd=data&format=json";

    fetch(url)
      .then((res) => res.json())
      .then((res) => {
        res = res["ISBN:" + isbn];
        setIsbnSearchBookdata(res);
        setIsbnSearchLoading(false);
        setIsbnSearchLoaded(true);
      });
  }

  return (
    <div>
      <CustomNavbar />

      <div className="content">
        <div className="div-booksearch">
          <input placeholder="Book name" />
          <ul></ul>
        </div>

        <div className="div-isbnsearch">
          <IsbnSearch />
        </div>
      </div>
      <Footer />
    </div>
  );
}
