import React from "react";

export default function BookSearch() {
  const [loading, setLoading] = React.useState(true);
  const [searchTitle, setSearchTitle] = React.useState("");
  const [searchAuthor, setSearchAuthor] = React.useState("");
  const [searchResults, setSearchResults] = React.useState(Object);

  React.useEffect(() => {
    const formattedTitle = searchTitle.replace(" ", "+");
    const formattedAuthor = searchAuthor.replace(" ", "+");
    const url = `http://openlibrary.org/search.json?title=${formattedTitle}&author=${formattedAuthor}`;

    fetch(url)
      .then((res) => res.json())
      .then((res) => {
        setSearchResults(res);
        setLoading(false);
        console.log(res);
      });
  });

  return (
    <div>
      <form onSubmit={submitSearch}>
        <input type="title" placeholder="Title" onChange={setSearchTitle} />
        <input type="title" placeholder="Author" onChange={setSearchAuthor} />
        <button type="submit" onSubmit={submitSearch}>
          Go
        </button>
      </form>
      <br />
      {loading
        ? ""
        : searchResults.docs.map((book) => {
            return book.title;
          })}
    </div>
  );
}
