import React from "react";
import LoadingIcon from "../elements/LoadingIcon";

export default function IsbnSearch() {
  const [isbn, setIsbn] = React.useState("0099922800");
  const [bookdata, setBookdata] = React.useState(Object);
  const [loading, setLoading] = React.useState(false);
  const [loaded, setLoaded] = React.useState(false);

  function handleSubmit(e) {
    e.preventDefault();
    setLoading(true);

    const url =
      "https://openlibrary.org/api/books?bibkeys=ISBN:" +
      isbn +
      "&jscmd=data&format=json";

    fetch(url)
      .then((res) => res.json())
      .then((res) => {
        res = res["ISBN:" + isbn];
        setBookdata(res);
        setLoading(false);
        setLoaded(true);
      });
  }

  return (
    <div>
      <h2>Book Search</h2>
      <form onSubmit={handleSubmit} className="isbn-search-form">
        <input
          placeholder="ISBN"
          value={isbn}
          onChange={(e) => setIsbn(e.target.value)}
        />
        <br />
        {loading ? " " : <button type="submit">Go</button>}
      </form>
      {loading ? (
        <LoadingIcon />
      ) : loaded ? (
        <div>
          <br />
          <p>
            Title: <b>{bookdata.title}</b>
          </p>
          <p>
            Pages: <b>{bookdata.number_of_pages}</b>
          </p>
          <p>
            Author(s): <b>{bookdata.authors.map((author) => author.name)}</b>
          </p>
          <p>Cover:</p>
          <img src={bookdata.cover.medium} alt="Book Cover"></img>
        </div>
      ) : (
        " "
      )}
    </div>
  );
}
