import React from "react";
import SubmitButton from "../elements/SubmitButton";
import LoadingIcon from "../elements/LoadingIcon";

export default function IsbnSearch() {
  return (
    <div>
      <h2>Book Search</h2>
      <form onSubmit={handleSubmit} className="isbn-search-form">
        <input
          placeholder="ISBN"
          value={isbn}
          onChange={(e) => setIsbn(e.target.value)}
        />
        <br />
        {isbnSearchLoading ? " " : <SubmitButton type="submit" text="Go" />}
      </form>
      {isbnSearchLoading ? (
        <LoadingIcon />
      ) : isbnSearchLoaded ? (
        <div>
          <br />
          <p>
            Title: <b>{IsbnSearchBookdata.title}</b>
          </p>
          <p>
            Pages: <b>{IsbnSearchBookdata.number_of_pages}</b>
          </p>
          <p>
            Author(s):{" "}
            <b>{IsbnSearchBookdata.authors.map((author) => author.name)}</b>
          </p>
          <p>Cover:</p>
          <img src={IsbnSearchBookdata.cover.medium} alt="Book Cover"></img>
        </div>
      ) : (
        " "
      )}
    </div>
  );
}
