import React from "react";
import "../styles/components/BookSearch.css";

export default function BookSearch() {
  const [loading, setLoading] = React.useState(true);
  const [searchTitle, setSearchTitle] = React.useState("");
  const [searchResults, setSearchResults] = React.useState(Object);

  function handleSubmit() {
    const formattedTitle = searchTitle.replace(" ", "+");
    const url = `http://openlibrary.org/search.json?title=${formattedTitle}`;

    fetch(url)
      .then((res) => res.json())
      .then((res) => {
        setSearchResults(res.docs);
        setLoading(false);
        console.log(res);
      });
  }

  return (
    <div>
      <form>
        <input
          type="title"
          placeholder="Title"
          onChange={(e) => setSearchTitle(e.target.value)}
        />
        <button onSubmit={handleSubmit}>Go</button>
      </form>
      <hr />
      <div className="book-suggestions">
        {loading
          ? ""
          : searchResults.slice(0, 5).map((book) => {
              return (
                <div className="search-suggestion">
                  <span>
                    {book.title} ({book.author_name[0]}) , &nbsp;
                    {book.publisher[0]} ({book.publish_date[0]})
                  </span>
                </div>
              );
            })}
      </div>
    </div>
  );
}
