import React from "react";
import "../styles/components/BookSearch.css";
import * as constants from "../constants";

export default function BookSearch() {
  const [loading, setLoading] = React.useState(true);
  const [query, setQuery] = React.useState("");
  const [searchResults, setSearchResults] = React.useState([]);

  React.useEffect(() => {
    const formattedQuery = query.replace(" ", "+");
    const url = `https://www.googleapis.com/books/v1/volumes?q=${formattedQuery}&key=${constants.google_books_api_key}`;

    if (formattedQuery.length >= 3) {
      fetch(url)
        .then((res) => res.json())
        .then((res) => {
          setSearchResults(res.items.slice(0, 5));
          setLoading(false);
          console.log(searchResults);
        });
    }
  });

  function findIsbn() {}

  return (
    <div>
      <h2>Book Search</h2>
      <input
        placeholder="Search"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
      />
      <div className="book-suggestions">
        {loading
          ? ""
          : searchResults.map((book) => {
              const bookInfo = book.volumeInfo;
              var bookIdentifier = "";
              if (bookInfo.industryIdentifiers) {
                bookIdentifier = bookInfo.industryIdentifiers[0].identifier;
              } else {
                bookIdentifier = "Cannot find ISBN";
              }
              return (
                <div className="searchSuggestion">
                  <span className="searchSuggestion-line title">
                    {bookInfo.title} - {bookInfo.authors[0]}
                  </span>
                  <span className="searchSuggestion-line publisher">
                    {bookInfo.publisher}, {bookInfo.publishedDate}
                  </span>
                  <span className="searchSuggestion-line isbn">
                    {bookIdentifier}
                  </span>
                </div>
              );
            })}
      </div>
    </div>
  );
}
